mapper.select:
SELECT m.id, m.name, m.alias, m.description, m.director_id, m.release_time, m.score, m.watch_num, m.movie_time, m.img_url FROM vm_movies m WHERE m.`status` = 1
AND m.name LIKE concat('%',#{query.keyword},'%')
AND m.id IN ( SELECT r.movie_id FROM vm_movies_tags_realation r WHERE r.`status` = #{query.status} and r.is_deleted = #{query.isDeleted} AND r.tag_id IN
#{item}
GROUP BY r.movie_id HAVING count(r.tag_id) >= #{query.tagIdsLength} )
order by ${page.orderBy} ${page.orderType}
limit #{page.start},#{page.size}

SELECT
count(m.id)
FROM
vm_movies m
WHERE
m.`status` = #{query.status}
and m.is_deleted = #{query.isDeleted}

    AND m.name LIKE concat('%',#{query.keyword},'%')


    AND m.id IN (
    SELECT
    r.movie_id
    FROM
    vm_movies_tags_realation r
    WHERE
    r.`status` = #{query.status}
    and r.is_deleted = #{query.isDeleted}
    AND r.tag_id IN

        #{item}

    GROUP BY
    r.movie_id
    HAVING
    count(r.tag_id) >= #{query.tagIdsLength}
    )

SELECT
DISTINCT
m.id,
m.name,
m.alias,
m.description,
m.director_id,
m.release_time,
m.score,
m.watch_num,
m.movie_time,
m.img_url
FROM
vm_movies m,
vm_movies_tags_realation tr
WHERE
m.`status` = #{query.status}
and m.is_deleted = #{query.isDeleted}
AND tr.`status` = #{query.status}
AND tr.is_deleted = #{query.isDeleted}
AND tr.movie_id = m.id
AND m.id !=#{query.excludeMovieId}
AND tr.tag_id IN

    #{item}


    order by ${page.orderBy} ${page.orderType}


    limit #{page.start},#{page.size}

SELECT
DISTINCT
m.id,
m.name,
m.alias,
m.description,
m.director_id,
m.release_time,
m.score,
m.watch_num,
m.movie_time,
m.img_url
FROM
vm_movies m,
vm_movies_filmmakers_realation mfr
WHERE
m.`status` = #{query.status}
and m.is_deleted = #{query.isDeleted}
AND mfr.`status` = #{query.status}
and mfr.is_deleted = #{query.isDeleted}
AND mfr.movie_id = m.id

    AND m.id !=#{query.excludeMovieId}

AND mfr.filmmaker_id in

    #{item}


    order by ${page.orderBy} ${page.orderType}


    limit #{page.start},#{page.size}

SELECT
m.*
FROM vm_movies m
where
m.is_deleted = #{query.isDeleted}

     m.`name` like concat('%',#{query.name},'%')


    order by ${page.orderBy} ${page.orderType}


    limit #{page.start},#{page.size}
